@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun DocumentAddChoiceScreen(
    onBackClick: () -> Unit,
    onScanOCR: () -> Unit,
    onManualEntry: () -> Unit
) {
    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Ajouter un Document") },
